import React, { useEffect, useRef } from 'react'
import { connect } from 'react-redux'
import styled from 'styled-components'

import * as actions from '../redux/actions'

const platforms = {
    PC: '4',
    iOS: '3',
    macOS: '5',
    Linux: '6',
    Android: '21',
    PlayStation: '18,16,15,27',
    XboxOne: '1,14,80',
    Nintendo: '7,9,8'

}

const Wrapper = styled.div`
    margin: 20px;
`
const Searchbox = styled.div`
    position:relative;

    input{
        width: calc(100% );
        outline:none;
        padding: 6px 15px;
        border-radius: 50px;
        box-sizing:border-box;
        border: none;
        margin-bottom:20px;
        font-size:20px;
        background-color: #5b5b5b;
        color: whitesmoke;
        transition: .5s;

        ::placeholder { 
            color: white;
            opacity: 1; 
            }

        &:hover{
            ::placeholder { 
            color: black;
            opacity: 1; 
            }
            color: black;
            background-color: white;
        }
        &:focus{
            color: black;
            background-color: white;
        }
    }

    button{
        position:absolute;
        right: 5px;
        top: 3px;
        padding: 5px 10px;
        border-radius: 50px;
        border: none;
        font-size: 16px;
        background-color: #9d9d9d;
        outline-style:none;

       &:hover{
           cursor: pointer;
           background-color: rgb(200,200,200);
       }
       &:active{
           cursor: pointer;
           background-color: rgb(230,230,230);
       }

    }
`


const Select = styled.div`
    position:relative;
`
const Listbox = styled.div`
    position: absolute;
`
const Selectname = styled.div`

`
export const Settings = ({ fetchItems, filter, order, word }) => {
    const input = useRef()

    useEffect(() => {
        fetchItems({})
    }, [])

    useEffect(() => {
        input.current.value = word
    })

    return (
        <Wrapper>
            <Searchbox>
                <input placeholder="Search games hereâ€¦" ref={input} type='text'></input>
                <button onClick={() => fetchItems({ word: input.current.value })}>Search</button>
            </Searchbox>
            <Select>
                <Selectname>Platforms</Selectname>
                {/* <Listbox>
                    {Object.entries(platforms).map((item, id) => <button
                        onClick={() => fetchItems({ word, order, filter: item[1] })}
                        key={id}>{item[0]}</button>)}
                </Listbox> */}
            </Select>
            <div>
                <Selectname>
                    OrderBy:
                </Selectname>
                {/* <Listbox>
                    <button onClick={() => fetchItems({ order: '-released' })}>Release date</button>
                    <button onClick={() => fetchItems({ order: '-rating' })}>Raiting</button>
                </Listbox> */}
            </div>
        </Wrapper>
    )
}

const mapStateToProps = (state) => ({
    filter: state.filter,
    order: state.order,
    word: state.searchWord
})

const mapDispatchToProps = (dispatch) => ({
    fetchItems: (x) => dispatch(actions.fetchItems(x))
})

export default connect(mapStateToProps, mapDispatchToProps)(Settings)
