import React, { useEffect, useRef } from 'react'
import styled from 'styled-components'
import { connect } from 'react-redux'

import Card from '../components/Card'
import * as actions from '../redux/actions'


const Wrapper = styled.div`
    display: flex;
    flex-wrap:wrap;
    justify-content:space-around;
`;



function Feed({ items, isFetching, fetchItems }) {

    const noFetch = useRef(false)

    const Items = () => <>{items.map((item, id) => <Card key={id} item={item} />)}</>

    useEffect(() => {
        let scrollHeight = Math.max(
            document.body.scrollHeight, document.documentElement.scrollHeight,
            document.body.offsetHeight, document.documentElement.offsetHeight,
            document.body.clientHeight, document.documentElement.clientHeight
        )

        document.addEventListener('scroll', foo)
        function foo() {
            console.log('Текущая прокрутка сверху: ', window.pageYOffset)
            console.log('Высота экрана: ', scrollHeight)
            if (scrollHeight == window.pageYOffset + document.documentElement.clientHeight) {
                console.log('yo')
                if (!noFetch.current) {
                    fetchItems({ reset: false })
                    noFetch.current = true;
                    setTimeout(() => { noFetch.current = false }, 2000)
                }

            }
        }

        return () => {
            document.removeEventListener('scroll', foo)
        }
    })

    return (
        <React.Fragment>
            <Wrapper>
                <Items />
            </Wrapper>
            {isFetching ? <div>Loading....</div> : null}
        </React.Fragment>
    )
}




const mapStateToProps = (state) => ({
    items: state.items,
    isFetching: state.isFetching
})

const mapDispatchToProps = (dispatch) => ({
    fetchItems: (x) => dispatch(actions.fetchItems(x))
})

export default connect(mapStateToProps, mapDispatchToProps)(Feed)
