//const { combineReducers } = require("redux")
import * as actions from './actions'
import { combineReducers } from 'redux'


const iniState = {
    items: [],
    filter: null,
    searchWord: null,
    order: null,
    isFetching: false,
}

const rootReducer = combineReducers({
    filter: filterReducer,
    searchWord: searchWordReducer,
    order: orderReducer,
    sort: sortReducer
})

export default rootReducer

function filterReducer(state = iniState.filter, action) {
    if (action.type === actions.SET_FILTER) {
        return action.filter
    }
    return state
}
function searchWordReducer(state = iniState.searchWord, action) {
    if (action.type === actions.SET_WORD) {
        return action.word
    }
    return state
}
function orderReducer(state = iniState.order, action) {
    if (action.type === actions.SET_ORDER) {
        return action.order
    }
    return state
}
function sortReducer(state = iniState.sort, action) {
    if (action.type === actions.SET_SORT) {
        return action.sort
    }
    return state
}
