import React, { useEffect, useRef } from 'react'
import { connect } from 'react-redux'

import * as actions from '../redux/actions'

const platforms = {
    PC: '4',
    iOS: '3',
    macOS: '5',
    Linux: '6',
    Android: '21',
    PlayStation: '18,16,15,27',
    XboxOne: '1,14,80',
    Nintendo: '7,9,8'

}

export const Settings = ({ fetchItems, filter, order, word }) => {
    const input = useRef()

    useEffect(() => {
        fetchItems({})
    }, [])

    useEffect(() => {
        input.current.value = word
    })

    return (
        <div>
            <div>
                <input ref={input} type='text'></input>
                <button onClick={() => fetchItems({ word: input.current.value })}>Search</button>
            </div>
            <div>
                FilterBy:
                <div>
                    {Object.entries(platforms).map((item, id) => <button
                        onClick={() => fetchItems({ word, order, filter: item[1] })}
                        key={id}>{item[0]}</button>)}
                </div>
            </div>

            <div>
                OrderBy:
                <button onClick={() => fetchItems({ order: '-released' })}>Release date</button>
                <button onClick={() => fetchItems({ order: '-rating' })}>Raiting</button>
            </div>
            <button onClick={() => fetchItems({})}>Search</button>
        </div>
    )
}

const mapStateToProps = (state) => ({
    filter: state.filter,
    order: state.order,
    word: state.searchWord
})

const mapDispatchToProps = (dispatch) => ({
    fetchItems: (x) => dispatch(actions.fetchItems(x))
})

export default connect(mapStateToProps, mapDispatchToProps)(Settings)
