import React from 'react'
import styled from 'styled-components'
import Link from 'next/link'


const Wrapper = styled.div`
    border: 1px solid grey;
    border-radius: 5px;
    margin: 10px 10px;
    max-width:300px;
    min-width:200px;
    flex: 1;
`

const Img = styled.img`
    width:100%;
`



function Card({ item }) {
    // console.log(item)
    return (
        <Wrapper>
            <Img src={item.background_image} alt='' />
            <div>{item.rating}</div>
            <div>{(new Date(item.released)).toDateString()}</div>
            <div>{item.name}</div>
            <Link
                href={{
                    pathname: `/game/${item.slug}`,
                    query: {},
                }}
            >
                <a>View</a>
            </Link>
        </Wrapper>
    )
}

export default Card
