import React from 'react'

import styled from 'styled-components'

const Select = styled.div`
    position:relative;
    display: inline-block;
    margin-right: 10px;
`
const Listbox = styled.div`
    position: absolute;
    top:0;
    z-index:1;
    width: 100%;
    background-color:white;
    border-radius:5px;
    button{
        border:none;
        font-size: 16px;
        padding: 5px 10px;
        width: calc(100% - 10px);
        margin:5px 5px;
        display:block;
        box-sizing:border-box;
        border-radius: 5px;
        &:hover{
            background-color:rgb(200,200,200);
            cursor: pointer;
        }
    }
`
const Selectname = styled.div`
    background-color:#444;
    width: 150px;
    padding: 5px 15px;
    border-radius: 5px;

`

function Selectable(props) {
    return (
        <Select>
            <Selectname>Platforms</Selectname>
            <Listbox>
                {props.items.map((item, id) => <div><button
                    onClick={() => fetchItems({ word, order, filter: item[1] })}
                    key={id}>{item[0]}</button></div>)}
            </Listbox>
        </Select>
    )
}

export default Selectable
