import React, { useState, useRef } from 'react'
import { connect } from 'react-redux'
import styled from 'styled-components'
import * as actions from '../redux/actions'


const Select = styled.div`
    position:relative;
    display: inline-block;
    margin-right: 10px;
`
const Listbox = styled.div`
    display:${props => props.open ? 'block' : 'none'};
    position: absolute;
    top:0;
    z-index:1;
    width: 100%;
    background-color:white;
    border-radius:5px;
    button{
        border:none;
        font-size: 16px;
        padding: 5px 10px;
        width: calc(100% - 10px);
        margin:5px 5px;
        display:block;
        box-sizing:border-box;
        border-radius: 5px;
        &:hover{
            background-color:rgb(200,200,200);
            cursor: pointer;
        }
    }
`
const Selectname = styled.div`
    background-color:#444;
    width: 150px;
    padding: 10px 15px;
    border-radius: 5px;

`

function Selectable({ title, items, word, fetchItems, filter, order, queryParam }) {

    const [open, setOpen] = useState(false)
    const listRef = useRef()

    function handleSelect(close) {

        if (close) {
            setOpen(false)
            return
        }
        setOpen(true)
        const specifiedElement = listRef.current;
        document.addEventListener('click', func);

        function func(event) {
            var isClickInside = specifiedElement.contains(event.target);

            if (!isClickInside) {
                //the click was outside the specifiedElement, do something
                setOpen(false)
                document.removeEventListener('click', func)
            }
        }
    }


    return (
        <Select>
            <Selectname
                onClick={() => { console.log('yo'); handleSelect() }}
            >{title}</Selectname>
            <Listbox open={open} ref={listRef}>
                {items.map((item, id) => {
                    let o = {}
                    o[queryParam] = item[1]

                    return <div key={id}><button
                        onClick={() => {
                            fetchItems(Object.assign({ word, order, filter }, o))
                            handleSelect(true)
                        }}
                    >{item[0]}</button></div>
                })}
            </Listbox>
        </Select >
    )
}

const mapStateToProps = (state) => ({
    filter: state.filter,
    order: state.order,
    word: state.searchWord
})

const mapDispatchToProps = (dispatch) => ({
    fetchItems: (x) => dispatch(actions.fetchItems(x))
})

export default connect(mapStateToProps, mapDispatchToProps)(Selectable)
