import React from 'react'
import styled from 'styled-components'
import Link from 'next/link'

const Wrapper = styled.div`
        position:relative;
        border-radius: 10px;
        margin: 10px 10px;
        max-width:400px;
        min-width:300px;
        flex: 1;
        background-color:#2b2b2b;
        transition: all .5s;
        &:hover{
            transform: scale(1.05);
        }
        `
let Img = styled.div`
        width:100%;
        height:200px;
        background-size: cover;
        background-position:top;
        border-top-left-radius:5px;
        border-top-right-radius:5px;
    `
const Content = styled.div`
    padding:10px;
    h2{
        margin:5px 0px;
    }

    button{
        width:100%;
        box-sizing:border-box;
        margin-top: 20px;
        outline-style:none;
        border: none;
        font-size:16px;
        padding: 10px;
        border-radius: 10px;
        color: white;
        background-color: #444;
        font-weight: lighter;
        &:hover{
            cursor: pointer;
            background-color: #555;
        }
    }
`

const Raiting = styled.div`



        position: absolute;
        top:0;
        right:0;
        width: 40px;
        height: 40px;
        background-color:red;
        z-index:1;
        div{
            position: absolute;
            top:0;
            right:0;
            z-index:2;
        }
`

function Card({ item }) {
    // console.log(item)
    Img = styled(Img)`
        background-image:url(${item.background_image});
    `
    return (
        <Wrapper>
            <Img />
            <Content>

                <h2>{item.name}</h2>
                <Raiting>
                    <div>{item.rating}</div>

                </Raiting>


                <div>{(new Date(item.released)).toDateString()}</div>

                <Link
                    href={{
                        pathname: `/game/${item.slug}`,
                        query: {},
                    }}
                >
                    <button>View</button>
                </Link>
            </Content>
        </Wrapper>
    )
}

export default Card
