import React, { useEffect, useState } from 'react'
import { useRouter } from 'next/router'
import styled from 'styled-components'

import store from '../../redux/store'
import Slider from '../../components/Slider'

const Column = styled.div`
float:left;
    width:30%;
    @media (max-width: 1189px){
        width:100%;
    }

`



const Game = () => {

    const [slides, setSlides] = useState(null)
    const [{ name, description_raw, website, background_image, rating, released }, setDetails] = useState({})

    const router = useRouter()
    const { slug } = router.query


    //screenshots
    useEffect(() => {

        if (slug) {
            const scrURL = 'https://api.rawg.io/api/games/' + slug + '/screenshots'

            fetch(scrURL)
                .then(x => {

                    if (!x.ok) throw new Error(x)
                    //console.log(x)
                    return x.json()
                })
                .then(x => setSlides(x.results))
                .catch(x => console.log(x))

            return () => {

            }
        }

    }, [])


    //screenshots
    useEffect(() => {
        if (slug) {
            const scrURL = 'https://api.rawg.io/api/games/' + slug

            fetch(scrURL)
                .then(x => {
                    if (!x.ok) throw new Error(x)
                    console.log(x)
                    return x.json()
                })
                .then(x => { console.log(x); setDetails(x) })
                .catch(x => console.log(x))

            return () => {

            }
        }

    }, [])



    return <div>
        {/* <img src={background_image} alt='' width='300px' /> */}
        <Slider slides={slides} />
        <Column>
            <h1>{name}</h1>
            <p>{rating}</p>
            <p>{released}</p>
            <p>{description_raw}</p>
            <a href={website} target={'_blank'}>Websilte</a>
        </Column>
    </div>
}



export const getServerSideProps = async ({ params }) => {
    const slug = params.slug
    return {
        props: { slug: params.slug }
    }
}



export default Game
