import React from 'react'
import styled from 'styled-components'
import Link from 'next/link'


const Wrapper = styled.div`
    border: 1px solid grey;
    border-radius: 5px;
    margin: 20px;
    padding:20px;
    max-width:400px;
    flex: 1;
`



function Card({ item }) {
    console.log(item)
    return (
        <Wrapper>
            <div>{item.name}</div>
            <img src={item.background_image} alt='' width='200px' />
            <div>{item.rating}</div>
            <div>{(new Date(item.released)).toDateString()}</div>

            <Link
                href={{
                    pathname: `/game/${item.slug}`,
                    query: {},
                }}
            >
                <a>View</a>
            </Link>
        </Wrapper>
    )
}

export default Card
