export const SET_FILTER = 'SET_FILTER'
export const SET_WORD = 'SET_WORD'
export const SET_ORDER = 'SET_ORDER'
export const SET_IS_FETCHING = 'SET_IS_FETCHING'
export const SET_IS_NOT_FETCHING = 'SET_IS_NOT_FETCHING'
export const ADD_ITEMS = 'ADD_ITEMS'
export const RESET_ITEMS = 'RESET_ITEMS'
export const SET_FETCHING_ERROR = 'SET_FETCHING_ERROR'


const URL = 'https://api.rawg.io/api/games?'

export function setFilter(filter) {
    return {
        type: SET_FILTER,
        filter
    }
}

export function setWord(word) {
    return {
        type: SET_WORD,
        word
    }
}
export function setOrder(order) {
    return {
        type: SET_ORDER,
        order
    }
}
export function setIsFetching() {
    return {
        type: SET_IS_FETCHING
    }
}
export function setIsNotFetching() {
    return {
        type: SET_IS_NOT_FETCHING
    }
}
export function addItems(items) {
    return {
        type: ADD_ITEMS,
        items
    }
}
export function resetItems(items) {
    return {
        type: RESET_ITEMS,
        items
    }
}



/////////////////////////////////////////               ASYNC ACTIONS


export function fetchPosts() {

    return (dispatch) => {

        fetch(url)
            .then(res => res.json())
            .then(res => dispatch(resetItems(res.results)))

    }

}
