import React from 'react'
import styled from 'styled-components'
import Link from 'next/link'


const Wrapper = styled.div`
    border: 1px solid grey;
    border-radius: 5px;
    margin: 20px;
    padding:20px;
`

function Card({ item }) {
    return (
        <Wrapper>
            <div>{item.name}</div>
            <div>{item.rating}</div>
            <div>{item.released}</div>
            <div>{item.name}</div>
            <Link
                href={{
                    pathname: `/game/${item.slug}`,
                    query: { param: item.slug, screenshots: item.short_screenshots },
                }}
            >
                <a>View</a>
            </Link>
        </Wrapper>
    )
}

export default Card
