import React from 'react'
import styled from 'styled-components'
import Link from 'next/link'

const Wrapper = styled.div`
        border: 1px solid grey;
        border-radius: 5px;
        margin: 10px 10px;
        max-width:400px;
        min-width:300px;
        flex: 1;
        background-color:#202020;
        `
let Img = styled.div`
        width:100%;
        height:200px;

        background-size: cover;
        background-position:top;
        border-top-left-radius:5px;
        border-top-right-radius:5px;
    `
const Content = styled.div`
    padding:10px;
`

function Card({ item }) {
    // console.log(item)
    Img = styled(Img)`
        background-image:url(${item.background_image});
    `
    return (
        <Wrapper>
            <Img />
            <Content>
                <div>{item.rating}</div>
                <div>{(new Date(item.released)).toDateString()}</div>
                <div>{item.name}</div>
                <Link
                    href={{
                        pathname: `/game/${item.slug}`,
                        query: {},
                    }}
                >
                    <a>View</a>
                </Link>
            </Content>
        </Wrapper>
    )
}

export default Card
