import Slider from '../../components/Slider'



const Game = () => {

    const [slides, setSlides] = useState(null)
    const [{ name, description_raw, website }, setDetails] = useState({})

    const router = useRouter()
    const { slug } = router.query


    //screenshots
    useEffect(() => {
        console.log(slug)
        if (slug) {
            const scrURL = 'https://api.rawg.io/api/games/' + slug + '/screenshots'

            fetch(scrURL)
                .then(x => {
                    console.log(x)
                    if (!x.ok) throw new Error(x)
                    console.log(x)
                    return x.json()
                })
                .then(x => setSlides(x.results))
                .catch(x => console.log(x))

            return () => {

            }
        }

    }, [])


    //screenshots
    useEffect(() => {
        if (slug) {
            const scrURL = 'https://api.rawg.io/api/games/' + slug

            fetch(scrURL)
                .then(x => {
                    if (!x.ok) throw new Error(x)
                    console.log(x)
                    return x.json()
                })
                .then(x => { console.log(x); setDetails(x) })
                .catch(x => console.log(x))

            return () => {

            }
        }

    }, [])



    return <div>
        <h1>{name}</h1>
        <Slider slides={slides} />
        <p>{description_raw}</p>
        <a href={website} target={_blank
        }>Websilte</a>
    </div>
}



export const getServerSideProps = async ({ params }) => {
    const slug = params.slug
    return {
        props: { slug }
    }
}



export default Game
