export const Settings = ({ fetchItems, filter, order, word }) => {
    const input = useRef()

    useEffect(() => {
        fetchItems({})
    }, [])

    useEffect(() => {
        input.current.value = word
    })

    function handleSelect(e) {
        const specifiedElement = e.target;
        document.addEventListener('click', function (event) {
            var isClickInside = specifiedElement.contains(event.target);

            if (!isClickInside) {
                //the click was outside the specifiedElement, do something
                console.log('outside!!!')
            }
        });
    }

    return (
        <Wrapper>
            <Searchbox>
                <input placeholder="Search games here…" ref={input} type='text'></input>
                <button onClick={() => fetchItems({ word: input.current.value })}>Search</button>
            </Searchbox>
            <Selectable items={Object.entries(platforms)} />
            {/* <Select>
                <Selectname onClick={(e) => handleSelect(e)}>Platforms</Selectname>
                <Listbox>
                    {Object.entries(platforms).map((item, id) => <div><button
                        onClick={() => fetchItems({ word, order, filter: item[1] })}
                        key={id}>{item[0]}</button></div>)}
                </Listbox>
            </Select> */}
            {/* <Select>
                <Selectname>
                    OrderBy:
                </Selectname>
                <Listbox>
                    <button onClick={() => fetchItems({ order: '-released' })}>Release date</button>
                    <button onClick={() => fetchItems({ order: '-rating' })}>Raiting</button>
                </Listbox>
            </Select> */}
        </Wrapper>
    )
}

const mapStateToProps = (state) => ({
    filter: state.filter,
    order: state.order,
    word: state.searchWord
})

const mapDispatchToProps = (dispatch) => ({
    fetchItems: (x) => dispatch(actions.fetchItems(x))
})

export default connect(mapStateToProps, mapDispatchToProps)(Settings)
