import React, { useState, useRef } from 'react'
import { connect } from 'react-redux'
import styled from 'styled-components'
import * as actions from '../redux/actions'


const Select = styled.div`
    position:relative;
    display: inline-block;
    margin-right: 10px;
`
const Listbox = styled.div`
    display:${props => props.open ? 'block' : 'none'};
    position: absolute;
    top:0;
    z-index:1;
    width: 100%;
    background-color:white;
    border-radius:5px;
    button{
        border:none;
        font-size: 16px;
        padding: 5px 10px;
        width: calc(100% - 10px);
        margin:5px 5px;
        display:block;
        box-sizing:border-box;
        border-radius: 5px;
        &:hover{
            background-color:rgb(200,200,200);
            cursor: pointer;
        }
    }
`
const Selectname = styled.div`
    background-color:${props => props.active ? '#fff' : '#444'};

    width: 150px;
    padding: 10px 15px;
    border-radius: 5px;
    &:hover{
        background-color:#555;
        cursor:pointer; 
    }
`

function Selectable({ title, items, word, fetchItems, filter, order, queryParam, active }) {
    console.log(active)
    const [open, setOpen] = useState(false)
    const listRef = useRef()
    const btnRef = useRef([])

    function handleSelect(close) {


        setOpen(true)


        document.addEventListener('click', func);

        function func(event) {

            let x = false
            for (let it of listRef.current.children) {

                if (it.contains(event.target)) { console.log('yo'); x = true; }
            }

            var isClickInside = listRef.current.contains(event.target);

            if (!isClickInside || x) {
                //the click was outside the specifiedElement, do something

                setOpen(false)
                document.removeEventListener('click', func)
            }
        }
    }

    return (
        <Select>
            <Selectname
                active={active}
                onClick={() => handleSelect()}
            >{title} &nabla;</Selectname>
            <Listbox open={open} ref={listRef}>
                {items.map((item, id) => {
                    let o = {}
                    o[queryParam] = item[1]

                    return <div key={id}><button
                        ref={btnRef[id]}
                        onClick={() => {
                            fetchItems(Object.assign({ word, order, filter }, o))

                        }}
                    >{item[0]}</button></div>
                })}
            </Listbox>
        </Select >
    )
}

const mapStateToProps = (state) => ({
    filter: state.filter,
    order: state.order,
    word: state.searchWord
})

const mapDispatchToProps = (dispatch) => ({
    fetchItems: (x) => dispatch(actions.fetchItems(x))
})

export default connect(mapStateToProps, mapDispatchToProps)(Selectable)
